import React, { useEffect, useState } from 'react'
import SubLoader from '../subLoader/SubLoader'
import axios from 'axios';


const OllUsers = () => {

  const [loading, setLoading] = useState(false)
  const [users, setUsers] = useState([])

  useEffect(() => {
    loadData()
 }, [])

  const loadData = async() => {
    setLoading(true)
    try {
      const response = await axios('http://localhost:5000/users')
      const usersData = response.data.map(user => {
        return {
          ...user,
          balance: user.balance ? user.balance.$numberDecimal : '0' 
        };
      });
      
      setUsers(usersData)
      console.log(response.data)
    } catch(e) {
       console.log(e)
    } finally {
        setLoading(false)
    }
  }

  if(loading) {
    return <SubLoader/>
  }



  return (
    <div className='ollUsers-con'>
        {users.length > 0 ? (
     <div className='ollUsersCard-con'>
          {users.map(user => 
            <div key={user._id} className='OllUsers-card'>
                <p>{user.name}</p>
                <p>{user.email}</p>
                <p>{user.balance}</p>
            </div>
          )}      
     </div>
        ) : (
            <div>
                <p>No users</p>
            </div>
        )}
    </div>
  )
}

export default OllUsers
